TEXT = ''''''

T1 = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'
T2 = 'bvwbjplbgvbhsrlpgdmjqwftvncz'
T3 = 'nppdvjthqldpwncqszvftbrmjlhg'

U1 = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'
U2 = 'bvwbjplbgvbhsrlpgdmjqwftvncz'
U3 = 'nppdvjthqldpwncqszvftbrmjlhg'

def p1(inp):
    for i, l in enumerate(zip(*(inp[i:] for i in range(4)))):
        if all((l.count(x) == 1 for x in l)):
            return i + 4

def p2(inp):
    for i, l in enumerate(zip(*(inp[i:] for i in range(14)))):
        if all((l.count(x) == 1 for x in l)):
            return i + 14

if __name__ == '__main__':

    #!|----------|
    #!| PART ONE |
    #!|----------|

    t1 = p1(T1)
    assert t1 == 7
    t2 = p1(T2)
    assert t2 == 5
    t3 = p1(T3)
    assert t3 == 6

    res = p1(TEXT)
    print(res)

    #!|----------|
    #!| PART TWO |
    #!|----------|

    t1 = p2(U1)
    assert t1 == 19
    t2 = p2(U2)
    assert t2 == 23
    t3 = p2(U3)
    assert t3 == 23

    res = p2(TEXT)
    print(res)